import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { buildWhatsAppShareUrl } from "../utils/share";

// ðŸ“Š Recharts import
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
} from "recharts";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }

    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    const fetchExpenses = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/expenses", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setExpenses(res.data);
      } catch (err) {
        console.error("Failed to fetch expenses:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchExpenses();
  }, [token, navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  if (loading) {
    return <p className="text-center mt-10">Loading dashboard...</p>;
  }

  const currentMonth = new Date().getMonth();
  const totalSpent = expenses
    .filter((exp) => new Date(exp.date).getMonth() === currentMonth)
    .reduce((acc, exp) => acc + exp.amount, 0);

  // âœ… Pure 12 mahine ke expense calculate
  const monthlyData = Array(12)
    .fill(0)
    .map((_, i) => {
      const monthTotal = expenses
        .filter((exp) => new Date(exp.date).getMonth() === i)
        .reduce((acc, exp) => acc + exp.amount, 0);

      return {
        month: new Date(2025, i).toLocaleString("default", { month: "short" }), // Jan, Feb ...
        total: monthTotal,
      };
    });

  return (
    <div className="p-6">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold">Dashboard</h1>
      </div>

      {user && (
        <div className="mt-4">
          <h2 className="text-xl font-semibold">Welcome, {user.name}</h2>
          <p className="text-center text-2xl font-bold text-primary mt-4 p-3 rounded-lg shadow-md">
            Monthly Limit: â‚¹{user.monthlyLimit || 0}
          </p>
        </div>
      )}

      {/* âœ… Progress Bar Section */}
      {user && user.monthlyLimit > 0 && (
        <div className="mt-8">
          <h2 className="text-xl font-semibold mb-2 text-center">
            Monthly Expense Progress
          </h2>

          {(() => {
            let progressPercent =
              user.monthlyLimit > 0
                ? Math.min((totalSpent / user.monthlyLimit) * 100, 100)
                : 0;

            let progressColor = "bg-green-500";
            if (progressPercent > 80) {
              progressColor = "bg-red-500";
            } else if (progressPercent > 50) {
              progressColor = "bg-orange-500";
            }

            return (
              <>
                <div className="w-full bg-gray-200 rounded-full h-6">
                  <div
                    className={`h-6 rounded-full ${progressColor}`}
                    style={{ width: `${progressPercent}%` }}
                  ></div>
                </div>
                <p className="text-center mt-2 text-lg font-bold text-primary">
                  Spent â‚¹{totalSpent} / â‚¹{user.monthlyLimit}
                </p>
              </>
            );
          })()}
        </div>
      )}

      {/* âœ… Bar Chart Section */}
      <div className="mt-10">
        <h2 className="text-xl font-semibold mb-4 text-center">
          Monthly Expenses (12 Months)
        </h2>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={monthlyData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="month" />
            <YAxis />
            <Tooltip />
            <Bar dataKey="total" fill="#3b82f6" radius={[6, 6, 0, 0]} />
          </BarChart>
        </ResponsiveContainer>
      </div>

      <div className="mt-6">
        <h2 className="text-xl font-semibold mb-2">Your Expenses</h2>
        {expenses.length === 0 ? (
          <p>No expenses found!</p>
        ) : (
          <table className="w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-800 text-primary">
                <th className="border border-gray-300 px-4 py-2">Title</th>
                <th className="border border-gray-300 px-4 py-2">Amount</th>
                <th className="border border-gray-300 px-4 py-2">Category</th>
                <th className="border border-gray-300 px-4 py-2">Date</th>
                <th className="border border-gray-300 px-4 py-2">Share</th>
              </tr>
            </thead>
            <tbody>
              {expenses
                .filter((exp) => {
                  const expDate = new Date(exp.date);
                  const now = new Date();
                  return (
                    expDate.getMonth() === now.getMonth() &&
                    expDate.getFullYear() === now.getFullYear()
                  );
                })
                .slice(-10)
                .map((exp) => (
                  <tr key={exp.id}>
                    <td className="border border-gray-300 px-4 py-2">
                      {exp.title}
                    </td>
                    <td className="border border-gray-300 px-4 py-2">
                      â‚¹{exp.amount}
                    </td>
                    <td className="border border-gray-300 px-4 py-2">
                      {exp.category}
                    </td>
                    <td className="border border-gray-300 px-4 py-2">
                      {exp.date}
                    </td>
                    <td className="border border-gray-300 px-4 py-2 text-center">
                      <a
                        href={buildWhatsAppShareUrl(exp)}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600"
                      >
                        Share
                      </a>
                    </td>
                  </tr>
                ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
