import { useEffect, useState } from "react";
import axios from "axios";

export default function GroupsOverview() {
  const [groups, setGroups] = useState([]);

  useEffect(() => {
    const loadGroups = async () => {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:5000/api/groups/all/expenses", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setGroups(res.data);
    };

    loadGroups();
  }, []);

  return (
    <div className="p-6 max-w-2xl mx-auto">
      <h2 className="text-2xl font-bold text-center text-primary mb-6">All Groups Overview</h2>

      {groups.map((group) => (
        <div key={group.groupId} className="mb-6 p-4 bg-gray-800 rounded-lg shadow">
          <h3 className="text-xl font-bold text-primary">{group.groupName}</h3>
          <p className="text-green-400 font-semibold">Total Spent: ₹{group.totalSpent}</p>

          {group.expenses.length === 0 ? (
            <p className="text-gray-400 text-sm mt-2">No expenses yet.</p>
          ) : (
            <ul className="mt-3 space-y-2">
              {group.expenses.map(exp => (
                <li key={exp.id} className="bg-gray-700 p-2 rounded">
                  <p className="text-gray-200">{exp.title} - ₹{exp.amount}</p>
                  <p className="text-gray-500 text-xs">{new Date(exp.createdAt).toLocaleString()}</p>
                </li>
              ))}
            </ul>
          )}
        </div>
      ))}
    </div>
  );
}
