export const buildWhatsAppShareUrl = (expense) => {
  const { amount, title, category, createdAt } = expense;

  const formattedTime = new Date(createdAt).toLocaleString(); // Converts to readable time

  const text = `ðŸ’° Expense Alert!

Title: ${title}
Amount: â‚¹${amount}
Category: ${category}
Time: ${formattedTime}

// Shared from Expense Tracker`;

  return `https://wa.me/?text=${encodeURIComponent(text)}`;
};
