import React, { useEffect, useState } from "react";
import { addExpenseToGroup, getGroupExpenses } from "../services/groupService.js";
import { useParams } from "react-router-dom";

export default function GroupDetail() {
  const { groupId } = useParams();
  const [expenses, setExpenses] = useState([]);
  const [desc, setDesc] = useState("");
  const [amount, setAmount] = useState("");

  useEffect(() => {
    loadExpenses();
  }, []);

  const loadExpenses = async () => {
    const res = await getGroupExpenses(groupId);
    setExpenses(res.data);
  };

  const handleAdd = async () => {
    await addExpenseToGroup(groupId, { description: desc, amount });
    setDesc("");
    setAmount("");
    loadExpenses();
  };

  return (
    <div className="p-6 max-w-lg mx-auto">
      <h1 className="text-xl font-bold mb-4">Group Expenses</h1>

      <div className="mb-4 space-y-2">
        <input
          value={desc}
          onChange={(e) => setDesc(e.target.value)}
          placeholder="Description"
          className="w-full p-2 border rounded"
        />
        <input
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Amount"
          type="number"
          className="w-full p-2 border rounded"
        />
        <button onClick={handleAdd} className="bg-green-600 text-white px-4 py-2 rounded">
          Add Expense
        </button>
      </div>

      <ul className="space-y-2">
        {expenses.map((exp) => (
          <li key={exp.id} className="border p-2 rounded bg-gray-100">
            {exp.description} - â‚¹{exp.amount}
          </li>
        ))}
      </ul>
    </div>
  );
}
