import axios from "axios";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";

export default function GroupInfo() {
  const { groupId } = useParams();
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get(
          `http://localhost:5000/api/groups/${groupId}/expenses`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setExpenses(res.data);
      } catch (err) {
        console.log("Error fetching expenses:", err);
      }finally {
        setLoading(false);
      }
    };

    fetchExpenses();
  }, [groupId]);

    if (loading) return <p className="text-center mt-6">Loading expenses...</p>;

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold mb-4">Group Expense History</h1>

      {expenses.length === 0 ? (
        <p>No expenses added yet.</p>
      ) : (
        <ul className="space-y-3">
          {expenses.map((exp) => (
            <li key={exp.id} className="bg-gray-800 p-3 rounded">
              <p>Title: {exp.title}</p>
              <p>Amount: â‚¹{exp.amount}</p>
              <p>Category: {exp.category}</p>
              <p>Date: {new Date(exp.createdAt).toLocaleString()}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
