import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { buildWhatsAppShareUrl } from "../utils/share";

export default function AddExpense() {
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("");
  const [date, setDate] = useState(""); 
  const [error, setError] = useState("");
  const [showShare, setShowShare] = useState(null); // ✅ naya state

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setError("Please login again.");
        return;
      }

      await axios.post(
        "http://localhost:5000/api/expenses",
        { title, amount, category, date },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert("✅ Expense added successfully!");

      // ✅ WhatsApp share data
      setShowShare({
        amount,
        merchant: title,
        time: new Date().toLocaleTimeString(),
      });
    } catch (err) {
      const msg =
        err?.response?.data?.message ||
        err?.message ||
        "Failed to add expense. Try again!";
      setError(msg);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900">
      <div className="bg-gray-800 p-8 rounded-lg w-[370px] shadow-lg">
        <h2 className="text-2xl font-bold mb-4 text-center text-primary">
          Add Expense
        </h2>
        {error && <p className="text-red-500 mb-3">{error}</p>}
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Expense Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full p-2 mb-3 rounded bg-gray-700 text-gray-200"
            required
          />
          <input
            type="number"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="w-full p-2 mb-3 rounded bg-gray-700 text-gray-200"
            required
          />
          <input
            type="text"
            placeholder="Category (Food, Travel, etc.)"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            className="w-full p-2 mb-3 rounded bg-gray-700 text-gray-200"
            required
          />
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full p-2 mb-4 rounded bg-gray-700 text-gray-200"
          />
          <button
            type="submit"
            className="w-full bg-primary text-black py-2 rounded font-semibold"
          >
            Add Expense
          </button>
        </form>

        {/* ✅ WhatsApp share button */}
        {showShare && (
          <a
            href={buildWhatsAppShareUrl(showShare)}
            target="_blank"
            rel="noreferrer"
            className="mt-3 inline-block w-full text-center bg-green-500 text-black px-3 py-2 rounded font-semibold"
          >
            Share on WhatsApp
          </a>
        )}

        <p
          className="text-center mt-3 text-primary cursor-pointer hover:underline"
          onClick={() => navigate("/dashboard")}
        >
          Back to Dashboard
        </p>
      </div>
    </div>
  );
}
