import React, { useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

export default function AddGroupExpense() {
  const { groupId } = useParams();
  const navigate = useNavigate();
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    // ✅ Step 1: Store in database
    await axios.post(
      `http://localhost:5000/api/groups/${groupId}/expense`,
      { title, amount, category },
      { headers: { Authorization: `Bearer ${token}` } }
    );

    // ✅ Step 2: Prepare UPI Intent
    const upiString = `upi://pay?pa=yourupiid@upi&pn=Group+Payment&am=${amount}&tn=${title}`;
    
    // ✅ Step 3: Try Google Pay first
    const gpayIntent = `intent:${upiString}#Intent;package=com.google.android.apps.nbu.paisa.user;end`;

    // ✅ Step 4: Fallback to PhonePe if Google Pay not found
    const phonepeIntent = `intent:${upiString}#Intent;package=com.phonepe.app;end`;

    // Try Google Pay
    const win = window.open(gpayIntent);

    setTimeout(() => {
      if (!win || win.closed || typeof win.closed === "undefined") {
        // Google Pay not installed → try PhonePe
        window.location.href = phonepeIntent;
      }
    }, 800);

    // ⚡ Redirect to Group Info after payment intent
    setTimeout(() => {
      navigate(`/groups/${groupId}/info`);
    }, 1500);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900">
      <div className="bg-gray-800 p-8 rounded-lg w-[370px] shadow-lg">
        <h2 className="text-2xl font-bold mb-4 text-center text-primary">
          Add Group Expense
        </h2>

        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Expense Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full p-2 mb-3 rounded bg-gray-700 text-gray-200"
            required
          />

          <input
            type="number"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="w-full p-2 mb-3 rounded bg-gray-700 text-gray-200"
            required
          />

          <input
            type="text"
            placeholder="Category (Food, Travel, etc.)"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            className="w-full p-2 mb-3 rounded bg-gray-700 text-gray-200"
            required
          />

          <button
            type="submit"
            className="w-full bg-primary text-black py-2 rounded font-semibold"
          >
            Add Expense & Pay
          </button>
        </form>
      </div>
    </div>
  );
}
