// client/src/pages/ExtraSpending.jsx
import React, { useEffect, useState } from "react";
import { getCurrentBudget } from "../services/budgetService.js";

export default function ExtraSpending() {
  const [data, setData] = useState(null);

  useEffect(() => {
    (async () => {
      const res = await getCurrentBudget();
      setData(res);
    })();
  }, []);

  if (!data) return <div className="p-6">Loading...</div>;

  return (
    <div className="max-w-xl mx-auto p-6 space-y-4">
      <h1 className="text-2xl font-bold">Extra Spending</h1>
      <div className="bg-gray-800 border border-gray-700 rounded p-4">
        <div>Month: <b>{data.month}</b></div>
        <div>Limit: ₹{Number(data.limitAmount || 0).toLocaleString()}</div>
        <div>Spent: ₹{Number(data.spent || 0).toLocaleString()}</div>
        <div className="text-red-400">
          Extra: ₹{Number(data.extra || 0).toLocaleString()}
        </div>
      </div>
    </div>
  );
}
