import React, { useEffect, useState } from "react";
import { createGroup, getGroups } from "../services/groupService.js";

const Groups = () => {
  const [groups, setGroups] = useState([]);
  const [groupName, setGroupName] = useState("");
  const [members, setMembers] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  // ✅ Groups fetch karo jab component load ho
  useEffect(() => {
    fetchGroups();
  }, []);

  const fetchGroups = async () => {
    try {
      const res = await getGroups();
      setGroups(res.data); // response se groups save
    } catch (err) {
      console.error("Failed to fetch groups:", err);
      setMessage("❌ Failed to fetch groups");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    try {
      await createGroup({
        name: groupName,
        members: members.split(",").map((m) => m.trim()),
      });
      setGroupName("");
      setMembers("");
      setMessage("✅ Group created successfully!");
      fetchGroups(); // ✅ naya group create hone ke baad refresh
    } catch (err) {
      console.error("Failed to create group:", err);
      setMessage("❌ Failed to create group");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Groups</h1>

      {/* ✅ Success / Error Message */}
      {message && <p className="mb-4 text-green-400">{message}</p>}

      {/* ✅ Create Group Form */}
      <form onSubmit={handleSubmit} className="mb-6">
        <input
          type="text"
          placeholder="Group Name"
          value={groupName}
          onChange={(e) => setGroupName(e.target.value)}
          className="border px-3 py-2 rounded-lg mr-2"
          required
        />
        <input
          type="text"
          placeholder="Members (comma separated)"
          value={members}
          onChange={(e) => setMembers(e.target.value)}
          className="border px-3 py-2 rounded-lg mr-2"
          required
        />
        <button
          type="submit"
          disabled={loading}
          className={`px-4 py-2 rounded-lg text-white ${
            loading ? "bg-gray-500" : "bg-blue-500"
          }`}
        >
          {loading ? "Creating..." : "Create Group"}
        </button>
      </form>

      {/* ✅ Groups List */}
      {groups.length === 0 ? (
        <p>No groups found.</p>
      ) : (
        <ul className="space-y-3">
          {groups
          
          .map((group) => (
            <li
              key={group._id}
              className="p-3 bg-gray-800 rounded-lg shadow text-white"
            >
              <h2 className="font-bold text-lg">{group.name}</h2>
             <p className="text-sm">
  Members: {Array.isArray(group.members) 
    ? group.members.join(", ") 
    : group.members}
</p>

            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Groups;
