import React, { useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

export default function AddGroupExpense() {
  const { groupId } = useParams();
  const navigate = useNavigate();
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    await axios.post(
      `http://localhost:5000/api/groups/${groupId}/expense`,
      { title, amount, category },
      { headers: { Authorization: `Bearer ${token}` } }
    );

    navigate(`/groups/${groupId}/info`); // ✅ Go back to group info page
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold mb-4">Add Group Expense</h1>

      <form onSubmit={handleSubmit}>
        <input className="block w-full mb-3 p-2" placeholder="Title" onChange={(e)=>setTitle(e.target.value)} required />
        <input className="block w-full mb-3 p-2" placeholder="Amount" type="number" onChange={(e)=>setAmount(e.target.value)} required />
        <input className="block w-full mb-3 p-2" placeholder="Category" onChange={(e)=>setCategory(e.target.value)} />

        <button onClick={() => navigate(`/add-expense/${group.id}`)} type="submit" className="bg-primary px-4 py-2 rounded">
          Add
        </button>
      </form>
    </div>
  );
}
