import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function AddExpense() {
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");

      await axios.post(
        "http://localhost:5000/api/expenses/add",
        { title, amount, category },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      alert("✅ Expense added successfully!");
      navigate("/dashboard"); // Add hone ke baad dashboard par redirect
    } catch (err) {
      setError("Failed to add expense. Try again!");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900">
      <div className="bg-gray-800 p-8 rounded-lg w-[350px] shadow-lg">
        <h2 className="text-2xl font-bold mb-4 text-center text-primary">
          Add Expense
        </h2>
        {error && <p className="text-red-500 mb-2">{error}</p>}
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Expense Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full p-2 mb-4 rounded bg-gray-700 text-gray-200"
            required
          />
          <input
            type="number"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="w-full p-2 mb-4 rounded bg-gray-700 text-gray-200"
            required
          />
          <input
            type="text"
            placeholder="Category"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            className="w-full p-2 mb-4 rounded bg-gray-700 text-gray-200"
            required
          />
          <button
            type="submit"
            className="w-full bg-primary text-black py-2 rounded font-semibold"
          >
            Add Expense
          </button>
        </form>
        <p
          className="text-center mt-3 text-primary cursor-pointer hover:underline"
          onClick={() => navigate("/dashboard")}
        >
          Back to Dashboard
        </p>
      </div>
    </div>
  );
}
