import React, { useEffect, useState } from "react";
import axios from "axios";

const Dashboard = () => {
  const [user, setUser] = useState({});
  const [newLimit, setNewLimit] = useState("");
  const [message, setMessage] = useState("");

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/user/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(res.data);
      } catch (err) {
        console.error("Error fetching user", err);
      }
    };
    fetchUser();
  }, [token]);

  const updateLimit = async () => {
    try {
      const res = await axios.put(
        "http://localhost:5000/api/user/update-limit",
        { monthlyLimit: newLimit },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage(res.data.message);
      setUser(res.data.user);
    } catch (err) {
      setMessage(
        err.response?.data?.error || "Failed to update monthly limit"
      );
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold text-center mb-6">Dashboard</h1>

      {/* Show Current Monthly Limit */}
      <p className="text-center text-lg font-semibold text-blue-600 mb-4">
        Monthly Limit: â‚¹{user.monthlyLimit || 0}
      </p>

      {/* Update Limit Form */}
      <div className="flex justify-center items-center gap-2 mb-4">
        <input
          type="number"
          placeholder="Enter new limit"
          className="border px-3 py-2 rounded-lg"
          value={newLimit}
          onChange={(e) => setNewLimit(e.target.value)}
        />
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
          onClick={updateLimit}
        >
          Update Limit
        </button>
      </div>

      {message && <p className="text-center text-red-500">{message}</p>}

      {/* Monthly Spent */}
      <p className="text-center text-lg font-semibold text-green-600">
        Spent This Month: â‚¹{user.currentMonthSpent || 0}
      </p>
    </div>
  );
};

export default Dashboard;
