import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function AddExpense() {
  const [item, setItem] = useState('');
  const [amount, setAmount] = useState('');
  const [category, setCategory] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        'http://localhost:5000/api/expenses',
        { item, amount, category },
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );
      alert('Expense added!');
      navigate('/dashboard');
    } catch (err) {
      console.error('Failed to add expense', err);
      alert('Error adding');
    }
  };

  return (
    <div className="min-h-screen flex justify-center items-center bg-gray-900">
      <div className="bg-gray-800 p-8 rounded-lg w-[350px]">
        <h2 className="text-2xl font-bold text-primary mb-4 text-center">Add Expense</h2>

        <form onSubmit={handleSubmit}>
          <input
            value={item}
            onChange={(e) => setItem(e.target.value)}
            placeholder="Item name (e.g., Shirt)"
            className="w-full p-2 mb-3 rounded bg-gray-700 text-gray-200"
            required
          />
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            placeholder="Amount (â‚¹)"
            className="w-full p-2 mb-3 rounded bg-gray-700 text-gray-200"
            required
          />
          <input
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            placeholder="Category (Food, Travel, etc.)"
            className="w-full p-2 mb-4 rounded bg-gray-700 text-gray-200"
          />
          <button
            type="submit"
            className="w-full bg-primary text-black py-2 rounded-md font-semibold"
          >
            Save Expense
          </button>
        </form>
      </div>
    </div>
  );
}
