export const buildWhatsAppShareUrl = (expense) => {
  const { amount, title, createdAt } = expense;

  // 1. Date Object Create karna
  const expenseDate = new Date(createdAt); 

  let formattedTime;

  // 2. Validity Check karna
  if (isNaN(expenseDate)) {
    // Agar Date Invalid hai, toh fallback text use karo
    formattedTime = "N/A (Time Invalid)"; 
    console.error("Error: 'createdAt' value is invalid or not recognized by new Date(). Value:", createdAt);
  } else {
    // Agar Date Valid hai, toh ek specific format use karo
    const options = { 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric', 
      hour: '2-digit', 
      minute: '2-digit', 
      timeZoneName: 'short'
    };
    // .toLocaleString() ki jagah .toLocaleDateString() with options use karo
    formattedTime = expenseDate.toLocaleString('en-IN', options); 
  }

  const text = `ðŸ’° Expense Alert!

Title: ${title}
Amount: â‚¹${amount}
Time: ${formattedTime}

Shared from Expense Tracker`;

  return `https://wa.me/?text=${encodeURIComponent(text)}`;
};
