import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Dashboard() {
  const navigate = useNavigate();
  const [monthlyLimit, setMonthlyLimit] = useState(0);
  const [totalSpent, setTotalSpent] = useState(0); // We will calculate later from transactions

  useEffect(() => {
    // Fetch user from localStorage
    const userJson = localStorage.getItem('user');
    if (userJson) {
      const userObj = JSON.parse(userJson);
      setMonthlyLimit(userObj.monthlyLimit || 0);
    }
  }, []);

  const remaining = monthlyLimit - totalSpent;

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-primary mb-4">Dashboard</h1>

      {/* Summary */}
      <div className="bg-gray-800 rounded-lg p-4 mb-6">
        <p>Total Spent This Month: ₹{totalSpent}</p>
        <p>Monthly Limit: ₹{monthlyLimit}</p>
        <p>Remaining: ₹{remaining}</p>
      </div>

      <button
        onClick={() => navigate('/add')}
        className="bg-primary text-black px-4 py-2 rounded-md"
      >
        Add Expense
      </button>
    </div>
  );
}
