import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // ✅ Token localStorage se nikalna
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/login"); // agar token nahi hai to login page pe bhej do
      return;
    }

    // ✅ User info decode karna (auth me backend ne return kiya tha)
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    // ✅ Expenses fetch karna
    const fetchExpenses = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/expenses", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setExpenses(res.data);
      } catch (err) {
        console.error("Failed to fetch expenses:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchExpenses();
  }, [token, navigate]);

  // ✅ Logout handler
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  if (loading) {
    return <p className="text-center mt-10">Loading dashboard...</p>;
  }

  return (
    <div className="p-6">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold">Dashboard</h1>
        {/* <button
          onClick={handleLogout}
          className="px-4 py-2 bg-red-500 text-white rounded-md"
        >
          Logout
        </button> */}
      </div>

      {user && (
        <div className="mt-4">
          <h2 className="text-xl font-semibold">Welcome, {user.name}</h2>
          <p>Email: {user.email}</p>
          <p>Monthly Limit: {user.monthlyLimit || 0}</p>
        </div>
      )}

      <div className="mt-6">
        <h2 className="text-xl font-semibold mb-2">Your Expenses</h2>
        {expenses.length === 0 ? (
          <p>No expenses found!</p>
        ) : (
          <table className="w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-primary-200 ">
                <th className="border border-gray-300 px-4 py-2">Title</th>
                <th className="border border-gray-300 px-4 py-2">Amount</th>
                <th className="border border-gray-300 px-4 py-2">Category</th>
                <th className="border border-gray-300 px-4 py-2">Date</th>
              </tr>
            </thead>
            <tbody>
              {expenses.map((exp) => (
                <tr key={exp.id}>
                  <td className="border border-gray-300 px-4 py-2">
                    {exp.title}
                  </td>
                  <td className="border border-gray-300 px-4 py-2">
                    ₹{exp.amount}
                  </td>
                  <td className="border border-gray-300 px-4 py-2">
                    {exp.category}
                  </td>
                  <td className="border border-gray-300 px-4 py-2">
                    {exp.date}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
