import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function Dashboard() {
  const navigate = useNavigate();

  // Dummy data for now (later we will fetch real transactions)
  const user = JSON.parse(localStorage.getItem('user'));
const monthlyLimit = user?.monthlyLimit || 0;
const totalSpent = 0; // Replace with real user data
  const remaining = monthlyLimit - totalSpent;

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-primary mb-4">Dashboard</h1>

      {/* Top summary */}
      <div className="bg-gray-800 rounded-lg p-4 mb-6">
        <p>Total Spent: ₹{totalSpent}</p>
        <p>Monthly Limit: ₹{monthlyLimit}</p>
        <p>Remaining: ₹{remaining}</p>
      </div>

      {/* TODO: charts section later */}

      <button
        onClick={() => navigate('/add')}
        className="bg-primary text-black px-4 py-2 rounded-md"
      >
        Add Expense
      </button>
    </div>
  );
}
