// client/src/pages/History.jsx
import React, { useEffect, useState } from "react";
import { getBudgetHistory } from "../services/budgetService";

export default function History() {
  const [rows, setRows] = useState([]);

  useEffect(() => {
    (async () => {
      const res = await getBudgetHistory();
      setRows(res || []);
    })();
  }, []);

  return (
    <div className="max-w-3xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-4">Budget History</h1>
      <div className="overflow-x-auto">
        <table className="w-full text-left border-collapse">
          <thead>
            <tr className="text-gray-300 border-b border-gray-700">
              <th className="py-2">Month</th>
              <th className="py-2">Limit</th>
              <th className="py-2">Spent</th>
              <th className="py-2">Extra</th>
              <th className="py-2">Changes</th>
            </tr>
          </thead>
          <tbody>
            {rows.length === 0 && (
              <tr>
                <td colSpan="5" className="py-4 text-gray-400">No history yet</td>
              </tr>
            )}
            {rows.map((r) => (
              <tr key={r.month} className="border-b border-gray-800">
                <td className="py-2">{r.month}</td>
                <td className="py-2">₹{Number(r.limitAmount || 0).toLocaleString()}</td>
                <td className="py-2">₹{Number(r.spent || 0).toLocaleString()}</td>
                <td className={`py-2 ${r.extra > 0 ? "text-red-400" : "text-gray-300"}`}>
                  ₹{Number(r.extra || 0).toLocaleString()}
                </td>
                <td className="py-2">{r.changesCount}/1</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
